package pip.database;import lombok.Data;import javax.persistence.*;@Entity@Data@Table(name = "quests")public class Quest {    @Id    @GeneratedValue(strategy= GenerationType.IDENTITY)    private Long id;    private String name;    @ManyToOne    @JoinColumn(name = "locations_id")    private Location location;    @ManyToOne    @JoinColumn(name = "actors_id")    private Actor questgiver;    private Integer reward;    @ManyToOne    @JoinColumn(name = "quests_id")    private Quest prerequisite;    @ManyToOne    @JoinColumn(name = "factions_id")    private Faction faction;    private String type;}