package pip.database;import lombok.Data;import javax.persistence.*;@Entity@Data@Table(name = "quests")public class Quest {    @Id    @GeneratedValue(strategy= GenerationType.IDENTITY)    public Long id;    public String name;    @ManyToOne    @JoinColumn(name = "locations_id")    public Location location;    @ManyToOne    @JoinColumn(name = "actors_id")    public Actor questgiver;    public Integer reward;    @ManyToOne    @JoinColumn(name = "quests_id")    public Quest prerequisite;    @ManyToOne    @JoinColumn(name = "factions_id")    public Faction faction;    public String type;}